package dataTypes;

public class dataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sayi = 1211118811;// eksi le artı 2 milyar'a kadar destekliyor
		sayi += 13;
		System.out.println(sayi);

		byte bSayı = 12;// -128 to 127
		bSayı = 127;
		System.out.println(bSayı);

		long longSayı = 122422222;// long int
		longSayı *= 1241221354;
		System.out.println(longSayı);

		double doubleSayı = 12.2422;
		doubleSayı += 5.6542;
		System.out.println(doubleSayı);

		char karekter = 'A';
		System.out.println(karekter);

		boolean dogruMu = false;// true or false
	}

}
