
public class whileUse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// while
		int i = 0;
		while (i <= 10) {
			System.out.println(i);
			i++;

		}
		System.out.println("while döngüsü bitti");
		// Do-While
		int j = 0;
		do {
			System.out.println(j);
			j += 2;

		} while (j <= 10);

	}

}
